import * as React from 'react';
import { useState } from 'react';
import TextComponent from '../components/Text';
import DropDown from '../components/DropDown/indetx';
import { Box, Flex } from '../components/FlexBox';
import styled from 'styled-components';
import { customSearch } from '../utils/customSearch';
import SearchHeader from '../components/SearchHeader';
const Wrapper = styled(Box)`
    width:1000px;
`;

const StyledImage = styled.img`
    width:300px;
    height:150px;
`;
const Tile = styled.a`
    width:300px;
    height:150px;
    background:#c4c4c4;
    margin: 20px 10px;
    &:hover ${StyledImage} {
        border: 2px solid red;
      }
`;

export interface SearchPageProps {

}

const SearchPage: React.SFC<SearchPageProps> = () => {
    const [selectedOption, setSelectedoption] = useState<string>('users');
    const [data, setData] = useState<any>([]);
    const handleSearchPage = async (value: string) => {
        const result = await customSearch(value, selectedOption);
        console.log(result)
        setData(result.items);
    }
    const handleDropDown = (newOption: string) => {
        console.log(newOption)
        setSelectedoption(newOption)
    }
    return (<Wrapper >
        <Flex flexWrap="wrap"
            flexDirection="column"
            alignItems="center"
            justifyContent="center"
            gridArea="main"
            overflow="hidden">
            <Flex justifyContent="center">
                <SearchHeader />
            </Flex>
            <Flex justifyContent="center" marginTop={10}>
                <TextComponent onInput={handleSearchPage} />
                <DropDown selectedOption={selectedOption} onDropDownChange={handleDropDown} />
            </Flex>

        </Flex>
        <Flex
            flexWrap="wrap"
            flexDirection="row"
            alignItems="center"
            justifyContent="center"
            gridArea="main"
            overflow="hidden"
        >
            {data.map((d: any) =>
                <Tile key={d.avatar_url} href={d.html_url} target="_blank">
                    <StyledImage src={d.avatar_url} />
                </Tile>
            )}
        </Flex>

    </Wrapper >
    );
}

export default SearchPage;
