import * as React from 'react';
import { useState } from "react";
import styled from "styled-components";

const StyledSelect = styled.select`
    font-size: larger;
    border: 1px solid #ccc;
    box-sizing: border-box;
    padding: 12px 20px;
    display: inline-block;
    color: grey;
    background: none;
    margin-left: 20px;
`;
const StyledOption = styled.option``;


export interface DropDownProps {

}

const options = [
    {
        id: '1', option: 'Users'
    },
    {
        id: '2', option: 'Repositories'
    }]

const DropDown: React.SFC<DropDownProps> = () => {
    const [selectedOption, setSelectedoption] = useState<string>('Users');
    return (<StyledSelect value={selectedOption} onChange={(e) => { selectedOption(e) }}>
        {options.map(o =>
            <StyledOption value={o.option}> {o.option}</StyledOption>
        )
        }
    </StyledSelect>);
}

export default DropDown;
