import styled from 'styled-components';

import {
  color, ColorProps, space, SpaceProps, layout, LayoutProps, flexbox, FlexProps, position, PositionProps,
} from 'styled-system';

type FlexProps = LayoutProps & FlexProps & ColorProps & SpaceProps & PositionProps;

export const Flex = styled.div<FlexProps>`
  boxsizing: 'border-box';
  minwidth: 0;
  display: flex;
  ${layout};
  ${flexbox};
  ${position};
  ${color};
  ${space};
`;
