import styled from 'styled-components';

import {
  color, ColorProps, space, SpaceProps, layout, LayoutProps, flexbox, FlexProps, position, PositionProps,
} from 'styled-system';

type Props = LayoutProps & FlexProps & ColorProps & SpaceProps & PositionProps;

export const Flex = styled.div<Props>`
  boxsizing: 'border-box';
  minwidth: 0;
  display: flex;
  ${layout};
  ${flexbox};
  ${position};
  ${color};
  ${space};
`;

const FlexComponent = forwardRef((props: any, ref: Ref<HTMLDivElement>) => <Flex ref={ref} {...props} />);

export default FlexComponent;
