import * as React from 'react';
import { useState } from 'react';
import TextComponent from '../components/Text';
import DropDown from '../components/DropDown';
import { Flex } from '../components/Flex';
import styled from 'styled-components';

const Wrapper = styled(Flex)`

`;
export interface SearchPageProps {

}

const SearchPage: React.SFC<SearchPageProps> = () => {
    const [selectedOption, setSelectedoption] = useState<string>('Users');
    const searchQueryURL = '  https://api.github.com/search/users/';

    const handleSearchPage = async (value: string) => {
        const response = await fetch(`${searchQueryURL}${value}`, {
            method: 'GET',
            credentials: 'same-origin',
            body: JSON.stringify({ value }),
            headers: {
                'Content-Type': 'application/json',
            },
        });
        const result = await response.json();
        console.log(result)
        return result;
    }
    const handleDropDown = (newOption: string) => {
        console.log(newOption)
        setSelectedoption(newOption)
    }
    return (<Wrapper >
        <TextComponent onInput={handleSearchPage} />
        <DropDown selectedOption={selectedOption} onDropDownChange={handleDropDown} />
    </Wrapper>
    );
}

export default SearchPage;
